function setUpHTMLFixture() {	setFixtures('<div id="content">'			+		'<time id="day-select">'			+			'<a href="#" id="button-previous" class="button">Previous month</a>'			+			'<p id="date-indicator">July 2016</p>'			+			'<a href="#" id="button-next"  class="button">Next month</a>'			+			'<div id="button-today">Today</div>'			+		'</time>'			+		'<div id="container">'			+			'<ol class="curr-days"></ol>'			+		'</div>'			+	'</div>');}describe("DOM TESTS:***************", function() {   describe("Button Click Event Tests", function() {	var spyEvent;	beforeEach(function() {	  setUpHTMLFixture();	});	it ("should invoke the next-event click event.", function() {	  spyEvent = spyOnEvent('#button-next', 'click');	  $('#button-next').trigger( "click" );	  expect('click').toHaveBeenTriggeredOn('#button-next');	  expect(spyEvent).toHaveBeenTriggered();	});	it ("should invoke the previous-button click event.", function() {	  spyEvent = spyOnEvent('#button-previous', 'click');	  $('#button-previous').trigger( "click" );	  expect('click').toHaveBeenTriggeredOn('#button-previous');	  expect(spyEvent).toHaveBeenTriggered();	});		it ("should invoke the date-indicator click event.", function() {	  spyEvent = spyOnEvent('#date-indicator', 'click');	  $('#date-indicator').trigger( "click" );	  expect('click').toHaveBeenTriggeredOn('#date-indicator');	  expect(spyEvent).toHaveBeenTriggered();	});  });  describe("Switch calendar months", function() {	var current, months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];	beforeEach(function() {	  setUpHTMLFixture();	  window.app.calendar.init();	});	it ("should display this month.", function() {	  expect($('#date-indicator')).toHaveText(months[new Date().getMonth()] + ' ' + new Date().getFullYear());	});			it ("should display the next month.", function() {	  $('#button-next').trigger('click');	  expect($('#date-indicator')).toHaveText(months[new Date().getMonth() + 1 ] + ' ' + new Date().getFullYear());	});  });});